    if (noteIndex !== -1) {
      notes[noteIndex] = {
        ...notes[noteIndex],
        title: formData.title.trim(),
        description: formData.description.trim(),
        tag: formData.tag || '',
        fileUrl: formData.fileUrl || '',
        fileName: formData.fileName || ''
      };
    }
