// Variables (mismo acceso que usas)
const position_work = context.component?.variables?.['3976cd11-062f-4d76-9ad2-e891575c93e2-value'];
const name_person = context.component?.variables?.['312c44f8-17e3-4753-a5df-2cc1766ac815-value'];
const name_seller = context.component?.variables?.['e7ea2611-f217-4b7f-9dd8-f6e7b511fb4e-value'];
const email = context.component?.variables?.['b0cb9ded-daa7-4841-a30f-5503072a6c5c-value'];
const password = context.component?.variables?.['6024d131-d217-4765-9e59-34a5613b9325-value'] || "";

// Lista completa de caracteres especiales ASCII
const specialChars = `!"#$%&'()*+,-./:;<=>?@[\\]^_\`{|}~`;

// ------------- Helpers en JS -------------
const isNotEmpty = v => v !== undefined && v !== null && String(v) !== "";
const hasMinLength = (v, n) => isNotEmpty(v) && String(v).length >= n;
const isEmail = (v) => /^\S+@\S+\.\S+$/.test(String(v || ""));
const hasUppercase = (v) => String(v).toLowerCase() !== String(v);
const hasSpecial = (pwd) => {
  if (!isNotEmpty(pwd)) return false;
  for (let ch of specialChars) {
    if (pwd.includes(ch)) return true;
  }
  return false;
};

// ------------- Validaciones -------------
const is_seller = isNotEmpty(position_work) && position_work === "Vendedor";

const validate_position_work = isNotEmpty(position_work);
const validate_name_person = isNotEmpty(name_person) && name_person.length >= 4;
const validate_name_seller = isNotEmpty(name_seller);
const validate_email = isNotEmpty(email) && isEmail(email);

const validate_password = isNotEmpty(password)
  && password.length >= 8
  && hasUppercase(password)
  && hasSpecial(password);

// ------------- Resultado (color) -------------
const ok = validate_position_work
  && ((!is_seller && validate_name_person) || (is_seller && validate_name_seller))
  && validate_email
  && validate_password;

return ok ? "Pointer" : "None";

