try {
    // Set loading state to true
    variables['25d2b99a-b254-4971-b357-aa93cfde1c71'] = true;

    // Get the user ID from the userToEdit object
    const userId = context.component?.props?.['userToEdit']?.['user_id'];

    if (!userId) {
        throw new Error('User ID not found in userToEdit object');
    }

    // Encode the user ID for URL
    const encodedUserId = encodeURIComponent(userId);

    // Build the complete URL
    const baseUrl = variables['2773a76d-9d96-4b7a-9db1-b4a00f551a21'];
    const url = `${baseUrl}/${encodedUserId}/roles`;

    // Prepare roles array
    const roleIds = ["rol_0VCDtsqSwgR8jUQR"];

    // Prepare the request payload
    const payload = {
        roles: roleIds
    };

    const response = await fetch(url, {
        method: 'DELETE',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload)
    });

    // Check if response is ok
    if (!response.ok) {
        const errorData = await response.text();
        throw new Error(`HTTP ${response.status}: ${errorData}`);
    }

    // Parse response data
    const responseData = await response.json();

    // Set loading state to false
    variables['25d2b99a-b254-4971-b357-aa93cfde1c71'] = false;

    return {
        success: true,
        data: responseData,
        completed: "Rol removido correctamente",
        statusCode: response.status
    };

} catch (error) {
    // Handle errors with more detailed information
    console.error('Error assigning roles:', error);

    // Set loading state to false
    variables['25d2b99a-b254-4971-b357-aa93cfde1c71'] = false;

    let errorMessage = 'Error desconocido';
    let statusCode = null;

    // Check if it's a fetch error with status
    if (error.message && error.message.includes('HTTP')) {
        const match = error.message.match(/HTTP (\d+):/);
        if (match) {
            statusCode = parseInt(match[1]);
            errorMessage = error.message;
        }
    } else {
        errorMessage = error.message || 'Error setting up request';
    }

    return {
        success: false,
        completed: "Rol sin remover",
        error: errorMessage,
        statusCode: statusCode,
        details: {
            userId: context.component?.props?.['userToEdit']?.['user_id'],
            url: `${variables['2773a76d-9d96-4b7a-9db1-b4a00f551a21']}/${encodeURIComponent(context.component?.props?.['userToEdit']?.['user_id'] || '')}/roles`,
            payload: {
                roles: ["rol_3vXHI50DTQuwJey0"]
            }
        }
    };
}

